mapper.select:
SELECT p.payment_id AS paymentId, u.username, u.phone_number AS phoneNumber, p.amount, p.status, p.payment_method AS paymentMethod, p.payment_time AS paymentTime FROM parking.payments p JOIN parking.users u ON p.user_id = u.user_id
AND u.phone_number = #{phoneNumber}
AND p.status = #{status}
ORDER BY paymentId ASC
